import './App.css';
import React, { useState, useRef } from 'react';



function App() {

  const [taskList, setTaskList] = useState([]);
  const textInput = useRef('');
  const timeInput = useRef('');
  //const message = useRef('');
  console.log(taskList)
  function Schedule(props) {
    function onDelete() {
      const newList = taskList.filter((i) => i !== (props.item, props.time));
      setTaskList(newList);
    }
    return (
      <h3>{props.item} at {props.time} <button onClick={onDelete}>Delete</button></h3>

    );
  }

  function onButtonClick() {
    let newItem = textInput.current.value;
    let newTime = timeInput.current.value;
    console.log(newItem);
    let newTask = [...taskList, (newItem, newTime)];
    setTaskList(newTask);
    textInput.current.value = "";
    timeInput.current.value = "";
  }
  return (
    <>
      <h1>Create Schedule</h1>

      <div class="idList" align="right">
        <h3>{taskList.map((i) => <Schedule item={i[0]} time={i[1]} />)}</h3>
      </div>
      <div class="editList" align="center">
        <input ref={textInput} type="text" />
        <input ref={timeInput} type="text" placeholder="Input time for task (ie 09:30 AM)" />
        <button onClick={() => onButtonClick()}> Add </button>

      </div>
    </>


  );
}

export default App;
