import './App.css';
import React, { useState, useRef } from 'react';



function App() {

  const [taskList, setTaskList] = useState(null);
  const textInput = useRef('');
  const message = useRef('');

  function Schedule(props) {
    function onDelete() {
      const newList = taskList.filter((i) => i !== props.item);
      setTaskList(newList);
    }
    return (
      <h3>{props.item} <button onClick={onDelete}>Delete</button></h3>

    );
  }

  function onButtonClick() {
    let newItem = textInput.current.value;
    console.log(newItem);
    let newTask = [...taskList, newItem];
    setTaskList(newTask);
    textInput.current.value = "";
  }
  return (
    <>
      <h1>Hello World</h1>

      <div class="idList" align="right">
        <h3>{taskList.map((i) => <Schedule item={i} />)}</h3>
      </div>
      <div class="editList" align="center">
        <input ref={textInput} type="text" />
        <button onClick={() => onButtonClick()}> Add </button>

      </div>
    </>


  );
}

export default App;
